import * as React from "react";
import styled from "styled-components";
import Avatar from "@mui/material/Avatar";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Icon from "./user.svg";
const mockAnimeLists = [
  {
    animeTitle: "ポケットモンスター",
    title: "ポケモンゲットだぜ",
    storyNum: 1,
    viewingApp: "dアニメストア",
    date: "5月14日",
  },
  {
    animeTitle: "からかい上手の高木さん",
    title: "クリスマス",
    storyNum: 10,
    viewingApp: "dアニメストア",
    date: "5月14日",
  },
];
const Home = () => {
  return (
    <Wrapper>
      <Left>
        <UserIcon src={Icon} />
        <div>ユーザー名：kiyo</div>
        <div>ユーザーID:53943r3</div>
      </Left>
      <Right>
        {mockAnimeLists.map((anime) => (
          <AnimeListItem>{anime.title}</AnimeListItem>
        ))}
      </Right>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  display: flex;
`;
const AvatarWrapper = styled.div`
  text-align: center;
  margin: 0 auto;
`;
const Left = styled.div`
  margin-left: 10px;
  border-style: solid;
  border-radius: 6px;
  border-color: gray;
  width: 400px;
  text-align: center;
`;
const Right = styled.div`
  margin-left: 10%;
`;
const AnimeListItem = styled.div``;
const UserIcon = styled.img`
  height: 40px;
  width: 40px;
  border-radius: 50%;
`;
export default Home;
