import styled from "styled-components";
import axios from "axios";
import { useState } from "react";
import Icon from "./user.svg";

const AddFriend = () => {
  const [searchWord, setSearchWord] = useState();
  const [findedUsers, setFindedUsers] = useState();

  const searchFriend = () => {
    axios({
      method: "GET",
      url: `http://localhost:8080/friend`,
      params: { searchWord },
    }).then((res) => {
      setFindedUsers(res.data.findedUsers);
    });
  };
  return (
    <>
      <InputArea>
        <p>フレンド検索：</p>
        <InputSearchWord
          type="text"
          name="searchWord"
          placeholder="idまたはユーザ名を入力してください"
          onChange={(event) => setSearchWord(event.target.value)}
        />
        <SearchButton onClick={() => searchFriend()}>検索</SearchButton>
      </InputArea>
      <div>ヒット件数：{findedUsers.length}</div>
      {findedUsers &&
        findedUsers.map((findedUser) => (
          <ListItem>
            <UserIcon src={Icon} />
            {findedUser.name}
          </ListItem>
        ))}
    </>
  );
};

const InputSearchWord = styled.input`
  width: 500px;
  height: 30px;
`;
const InputArea = styled.div`
  display: flex;
`;
const SearchButton = styled.button`
  height: 35px;
`;
const ListItem = styled.div`
  display: flex;
  border: solid #efeaea;
  margin: 10px;
  border-radius: 1em;
  height: 80px;
  align-items: center;
`;
const UserIcon = styled.img`
  height: 100px;
  width: 100px;
  border-radius: 50%;
`;
export default AddFriend;
