import * as React from "react";
import styled from "styled-components";
import Avatar from "@mui/material/Avatar";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Icon from "./user.svg";
const mockAnimeLists = [
  {
    animeTitle: "ポケットモンスター",
    title: "ポケモンゲットだぜ",
    storyNum: 1,
    viewingApp: "dアニメストア",
    date: "5月14日",
  },
  {
    animeTitle: "からかい上手の高木さん",
    title: "クリスマス",
    storyNum: 10,
    viewingApp: "dアニメストア",
    date: "5月14日",
  },
];
const Home = () => {
  return (
    <Wrapper>
      <Left>
        <UserIcon src={Icon} />
        <div>ユーザー名：kiyo</div>
        <div>ユーザーID:53943r3</div>
      </Left>
      <Right>
        {mockAnimeLists.map((anime) => (
          <ListContent>
            <UpperRowContent>
              <AnimeTitle>{anime.animeTitle}</AnimeTitle>
            </UpperRowContent>

            <LowerRowContent>
              <StoryNum>第{anime.storyNum}話</StoryNum>
              <Title>{anime.title}</Title>
              <p>at {anime.viewingApp}</p>
            </LowerRowContent>
          </ListContent>
        ))}
      </Right>
    </Wrapper>
  );
};

const UpperRowContent = styled.div`
  display: flex;
  margin: 0;
`;
const LowerRowContent = styled.div`
  display: flex;
  margin: 0;
`;
const ListContent = styled.div`
  margin-left: 10px;
  width: 90%;
`;
const StoryNum = styled.p`
  width: 25%;
`;
const Title = styled.p`
  width: 40%;
`;

const AnimeTitle = styled.p`
  margin: 10px 0 0 0;
  width: 50%;
`;
const Wrapper = styled.div`
  display: flex;
`;
const AvatarWrapper = styled.div`
  text-align: center;
  margin: 0 auto;
`;
const Left = styled.div`
  margin-left: 10px;
  border-style: solid;
  border-radius: 6px;
  border-color: gray;
  width: 400px;
  text-align: center;
`;
const Right = styled.div`
  margin-left: 10%;
  width: 100%;
`;
const UserIcon = styled.img`
  height: 200px;
  width: 200px;
  border-radius: 50%;
`;
export default Home;
