import { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import ReactStars from "react-stars";
import useFirebaseAuth from "../../../auth/useFirebaseAuth";
const AddFriendModal = (props) => {
  const { close } = props;
  const { currentUser } = useFirebaseAuth();
  const userId = currentUser.multiFactor.user.uid;
  const [title, setTitle] = useState();
  const [storyNum, setStoryNum] = useState();
  const [subTitle, setSubTitle] = useState();
  const [starRating, setStarRating] = useState();
  const [viewingApp, setViewingApp] = useState();
  return (
    <Wrapper>
      <CloseButton onClick={close}>CLOSE</CloseButton>
      <ProcessingContent>視聴アニメの追加</ProcessingContent>
      <TitleArea>
        <Text>タイトル：</Text>
        <InputText
          type="text"
          name="title"
          onChange={(event) => setTitle(event.target.value)}
        />
      </TitleArea>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 500px;
  height: 500px;
  background-color: white;
`;

const ProcessingContent = styled.p``;
const CloseButton = styled.button`
  margin-right: 10px;
  margin-left: auto;
`;

const TitleArea = styled.div`
  display: flex;
`;
const StoryNumArea = styled.div`
  display: flex;
`;
const SubTitleArea = styled.div`
  display: flex;
`;
const ViewingAppArea = styled.div`
  display: flex;
`;
const StarRatingArea = styled.div`
  display: flex;
`;
const InputText = styled.input`
  width: 500px;
  height: 50px;
  font-size: 20px;
`;
const Text = styled.p`
  font-size: 20px;
  font-weight: bold;
`;

export default AddFriendModal;
