import { useEffect, useState } from "react";
import List from "@mui/material/List";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import { Link } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import useFirebaseAuth from "../../auth/useFirebaseAuth";

const mockFirend = [
  {
    name: "井戸はるき",
    isWatching: true,
    subTitle: "ポケットモンスター",
    title: "ポケモンゲットだぜ",
    storyNum: 1,
    viewingApp: "dアニメストア",
  },
  {
    name: "赤沢きよと",
    isWatching: false,
    animeTitle: "からかい上手の高木さん",
    title: "クリスマス",
    storyNum: 10,
    viewingApp: "dアニメストア",
  },
];

const FriendList = () => {
  const { currentUser } = useFirebaseAuth();
  const userId = currentUser.multiFactor.user.uid;
  const [viewingList, setViewingList] = useState();
  useEffect(() => {
    axios({
      method: "GET",
      url: `http://localhost:8080/friend/friend-list`,
      params: { userId },
    }).then((res) => {
      setViewingList(res.data);
      console.log(res.data);
    });
  }, [userId]);
  console.log(viewingList);
  return (
    <Wrapper>
      <Link to="/add-friend">
        <TransitionAddAnime>フレンドの追加</TransitionAddAnime>
      </Link>
      {viewingList &&
        viewingList.map((viewingAnime) => {
          console.log(viewingAnime);
          <>
            <ListItem alignItems="flex-start">
              <AvatarWrapper>
                <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
              </AvatarWrapper>
              <ListContent>
                <UpperRowContent>
                  {/* <FriendName>{friend.name}</FriendName> */}
                  <AnimeTitle>{viewingAnime.title}</AnimeTitle>
                </UpperRowContent>

                <LowerRowContent>
                  <StoryNum>第{viewingAnime.story_number}話</StoryNum>
                  <Title>{viewingAnime.sub_title}</Title>
                  <p>at {viewingAnime.viewingApp}</p>
                </LowerRowContent>
              </ListContent>
            </ListItem>
          </>;
        })}
    </Wrapper>
  );
};

const Wrapper = styled(List)`
  width: 100%;
  max-width: 700;
`;
const AvatarWrapper = styled(ListItemAvatar)`
  margin-left: 15px;
`;
const ListItem = styled.div`
  display: flex;
  border: solid #efeaea;
  margin: 10px;
  border-radius: 1em;
  height: 80px;
  align-items: center;
`;
const UpperRowContent = styled.div`
  display: flex;
  margin: 0;
`;
const LowerRowContent = styled.div`
  display: flex;
  margin: 0;
`;
const ListContent = styled.div`
  margin-left: 10px;
  width: 90%;
`;
const FriendName = styled.p`
  margin: 10px 0 0 0;
  width: 25%;
`;
const AnimeTitle = styled.p`
  margin: 10px 0 0 0;
  width: 50%;
`;
const StoryNum = styled.p`
  width: 25%;
`;
const Title = styled.p`
  width: 40%;
`;
const Circle = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-right: 10px;
  margin-left: auto;
  background-color: ${(props) => (props.isWatching ? "green" : "red")};
`;

const TransitionAddAnime = styled.button`
  width: 200px;
  height: 56px;
  border: none;
  border-radius: 6px;
  background-color: green;
  color: white;
`;
export default FriendList;
