import { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import ReactStars from "react-stars";
import useFirebaseAuth from "../../../auth/useFirebaseAuth";
const AddFriendModal = (props) => {
  const { close } = props;
  const { currentUser } = useFirebaseAuth();
  const userId = currentUser.multiFactor.user.uid;
  const [title, setTitle] = useState();
  const [storyNum, setStoryNum] = useState();
  const [subTitle, setSubTitle] = useState();
  const [starRating, setStarRating] = useState();
  const [viewingApp, setViewingApp] = useState();
  const handleSubmit = async () => {
    const data = {
      userId,
      title,
      storyNum,
      subTitle,
      starRating,
      viewingApp,
    };
    await axios({
      method: "POST",
      url: `http://localhost:8080/personal/add-anime`,
      data,
    });
  };
  return (
    <Wrapper>
      <TopContent>
        <ProcessingContent>視聴アニメの追加</ProcessingContent>
        <CloseButton onClick={close}>x</CloseButton>
      </TopContent>

      <TitleArea>
        <Text>タイトル</Text>
        <InputText
          type="text"
          name="title"
          onChange={(event) => setTitle(event.target.value)}
        />
      </TitleArea>
      <StoryNumArea>
        <Text>話数：</Text>
        <InputNum
          type="text"
          name="title"
          onChange={(event) => setStoryNum(event.target.value)}
        />
      </StoryNumArea>
      <SubTitleArea>
        <Text>サブタイトル：</Text>
        <InputText
          type="text"
          name="title"
          onChange={(event) => setSubTitle(event.target.value)}
        />
      </SubTitleArea>
      <ViewingAppArea>
        <Text>視聴アプリ：</Text>
        <ViewingAppSelect name="住所">
          <option value="tokyo">東京都</option>
          <option value="kanagawa">神奈川県</option>
          <option value="chiba">千葉県</option>
          <option value="saitama">埼玉県</option>
          <option value="ibaraki">茨城県</option>
        </ViewingAppSelect>
        {/* <input
          type="text"
          name="title"
          onChange={(event) => setViewingApp(event.target.value)}
        /> */}
      </ViewingAppArea>
      <StarRatingArea>
        <Text>評価：</Text>
        <StyledStars
          count={5}
          size={24}
          color={"#fff"}
          onChange={(rating) => setStarRating(rating)}
        />
      </StarRatingArea>
      <ButtonsArea>
        <button
          onClick={() => {
            handleSubmit();
          }}
        >
          追加
        </button>
        <button
          onClick={() => {
            handleSubmit();
          }}
        >
          リセット
        </button>
      </ButtonsArea>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 500px;
  height: 500px;
  background-color: white;
  overflow: scroll;
  ::-webkit-scrollbar {
    /* Chrome, Safari 対応 */
    display: none;
  }
  padding: 10px;
  border-radius: 6px;
`;

const ProcessingContent = styled.p`
  margin: auto -20px auto auto;
  font-weight: bold;
  font-size: 30px;
`;
const CloseButton = styled.button`
  width: 40px;
  height: 40px;
  margin-right: 10px;
  margin-left: auto;
  border-radius: 50%;
  font-size: 20px;
  /* border: none; */
  background-color: white;
`;

const TitleArea = styled.div`
  display: flex;
`;
const StoryNumArea = styled.div`
  display: flex;
`;
const SubTitleArea = styled.div`
  display: flex;
`;
const ViewingAppArea = styled.div`
  display: flex;
`;
const StarRatingArea = styled.div`
  display: flex;
  margin: auto;
`;
const InputText = styled.input`
  width: 300px;
  height: 30px;
  font-size: 20px;
  margin: auto auto auto 0;
`;
const InputNum = styled.input`
  width: 50px;
  height: 30px;
  font-size: 20px;
  margin: auto auto auto 0;
`;
const Text = styled.p`
  font-size: 20px;
  width: 150px;
  margin-left: 30px;
`;
const ViewingAppSelect = styled.select`
  width: 100px;
  height: 30px;
  margin: auto auto auto 0;
`;
const TopContent = styled.div`
  display: flex;
  width: 100%;
  margin-top: 20px;
  margin-bottom: 20px;
`;
const StyledStars = styled(ReactStars)`
  margin: auto auto auto 0;
`;
const ButtonsArea = styled.div`
  display: flex;
`;
export default AddFriendModal;
